import React, { Component } from 'react'; 
// import PropTypes from 'prop-types';
import './CreateOrUpdate.css';

import * as PersonServices from '../services/person';

export class CreateOrUpdate extends Component {
   
    constructor(props){
        super(props);

        this.state = {
            person: {
                image:'data:image/jpeg;base64,',
                firstname: '',
                lastname: '',
                color: '',
                welcomeMsg: '',
                email:'', 
                emailError:'',
            },
            test: false,
            existPerson: false,
            textLength: this.props.maxLength,
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChar = this.handleChar.bind(this);
    }
    
    onSelectPerson = (email) => {
       if(email){
        PersonServices.displayPerson(email)
            .then(r => r.json())
            .then(person => this.setState({person}));
       }
    };

    componentDidMount(){
        this.onSelectPerson(this.props.match.params.email) 
        
        let isAdmin = JSON.parse(sessionStorage.getItem('isAdmin'));
        let userEmail = sessionStorage.getItem('userEmail');
        let existPerson= sessionStorage.getItem('existPerson');


        this.setState({
            isAdmin, userEmail, existPerson
        })

        console.log("sessionStorageC", isAdmin)
        console.log("userNameC", userEmail)
    }

    getImage = (e) => {
        const files = e.currentTarget.files;
        var reader = new FileReader();

        reader.onload = (e) => {
            const person = this.state.person;
            person.image = e.target.result;
            this.setState({person});
        }
        reader.readAsDataURL(files[0]);
    }

    handleChange = (e) => {
        console.log("handleChange", e.target.value)
        const {person} = this.state;
        person[e.target.id] = e.target.value;
        this.setState({person})
    }

    verifPerson = (email) => {
        return PersonServices.verifPerson(email)
            .then(r => r.json())
            .then(test => test);
    }

    handleSubmit = async (e) => {
        e.preventDefault();
        let postPerson = PersonServices.updatePerson(this.state.person);
        const isPresent = this.state.person.id;
        await this.verifPerson(this.state.person.email);
        console.log("emailtest",this.state.person.email);
        console.log("Mon test",this.state.test);
        
        let promesseResponse = postPerson
            .then((response) =>{
            console.log("status 200",response);
            return response.json();
        });
        
            {
                (!this.state.test ?
       
                    promesseResponse
                        .then((person)=>{
                        this.props.history.push("/trombinoscope");
                        let message1 = person.firstname + " " + person.lastname + " ayant \nla couleur préférée: " + person.color +
                                                        "\nle message de bienvenue: " + person.welcomeMsg +
                                                        "\nl'email: " + person.email + " a été créée.";
                        let message2 = "Les informations de " + person.firstname + " " + person.lastname +" ont été mises à jour.";
            
                        let message3 = ( isPresent ? message2 : message1 );
                        alert(message3);
                        })
                    .catch((response)=>{
                    console.log("error", response);
                    response.json().then((error)=>{
                    alert(error.message);
                    })
                    }) : alert("La persone existe deja")
                )
            }
    }

    goToHomePage = () => {
        this.props.history.push(`/trombinoscope`);
    }

    // limite du caractère
    handleChar = (e) => {
        console.log("handleChange", e.target.value)
        const {person} = this.state;
        person[e.target.id] = e.target.value;
        var length= e.target.value.length;

        this.setState({person, textLength : this.props.maxLength - length});
    }


    render(){
        const { person } = this.state;
        const { isAdmin, userEmail, existPerson} = this.state;
        const { test } = this.state
        const isPresent = person.id;

        console.log("sessionStorageC1", isAdmin)
        console.log("userNameC1", userEmail)
        console.log("existPersonCreate", existPerson)
        console.log("test", test)
        console.log("existPC", existPerson)

        return(
                <div className="App Add-form animated pulse" style={{marginBottom:'0px'}}>
                   
                        <form className="create" onSubmit={this.handleSubmit} >
                            <h4>{isPresent ? "Mettre à jour votre profil" : "Créer votre profil"}</h4>
                            
                            <p type="Prénom:"><input placeholder="Votre prénom.." id="firstname" value={person.firstname} onChange={this.handleChange.bind(this)} maxLength={30} required></input></p>
                            <p type="Nom:"><input placeholder="Votre nom.." id="lastname" value={person.lastname} onChange={this.handleChange.bind(this)} maxLength={30} required></input></p>

                            <p type="Profession:"><input placeholder="Votre profession.." id="occupation" value={person.occupation} onChange={this.handleChange.bind(this)}maxLength={30}  required></input></p>
                            <p type="Couleur préfrée:"><input type="color" id="color" value={person.color} onChange={this.handleChange.bind(this)} style={{height: "20px"}}></input></p>

                            <p type="Message d'accueil:"><textarea placeholder="Votre message de bienvenue..." id="welcomeMsg" value={person.welcomeMsg} onChange={this.handleChar.bind(this)} maxLength={30} style={{width:"100%", maxWidth: "100%", maxHeight: "46px", margin: "0 0 -13px"}}></textarea></p>
                            <div className="character">{this.state.textLength} caractère(s) restant(s)</div>

                            { (isAdmin ?
                            <p type="Email:"><input placeholder="Votre email.." id="email" type="email" value={person.email} onChange={this.handleChange.bind(this)} required></input></p>
                              : (person.email === userEmail && <span style={{margin: "10px"}}><p>Email : {userEmail}</p></span> ))
                            }
                            <img className="displayImg" src={person.image} alt="visage" responsive=""/>
                            <input className="" type="file" onChange={this.getImage} placeholder="Picture" accept="image/*" id="image"/>

                            <button>{isPresent ? "Mettre à jour :)" : "Ajouter :)"}</button> {''}
                            
                            <button onClick={ () => { this.goToHomePage()} }>Annuler</button>
                        </form>
                </div>
            )
    }
}
